;(function(oldConsole) {
  const { shieldEl, swordEl, statsEl } = _createStatsDisplay()
  document.body.appendChild(statsEl)

  let armor = 0
  let damage = 0

  _addItemPickupListener((item => {
    armor += item.armour ?? 0
    damage += item.dmg ?? 0

    updateStats(shieldEl, swordEl, statsEl)
  }))
  updateStats(shieldEl, swordEl, statsEl)

  function updateStats(shieldEl, swordEl, statsEl) {
    // https://developer.mozilla.org/en-US/docs/Web/API/Node/removeChild
    while (statsEl.firstChild) {
      statsEl.removeChild(statsEl.firstChild);
    }

    const armorStat = _createIndividualStatDisplay(shieldEl, armor)
    const damageStat = _createIndividualStatDisplay(swordEl, damage)

    statsEl.appendChild(armorStat)
    statsEl.appendChild(damageStat)
  }

  function _createIndividualStatDisplay(statIconEl, statValue) {
    const valueEl = document.createElement('span')
    valueEl.style.cssText = 'vertical-align: middle;'
    valueEl.textContent = `+${statValue}`

    const statEl = document.createElement('div')

    statEl.appendChild(statIconEl)
    statEl.appendChild(valueEl)

    return statEl
  }

  function _createStatsDisplay() {
    const shieldEl = document.createElement('img')
    shieldEl.style.cssText = 'width: 28px; height: 28px; margin-right: 8px; vertical-align: middle;'
    shieldEl.src =
      'data:image/svg+xml;base64,'

    const swordEl = document.createElement('img')
    swordEl.style.cssText = 'width: 28px; height: 28px; margin-right: 8px; vertical-align: middle;'
    swordEl.src = 'data:image/svg+xml;base64,'

    const statsEl = document.createElement('div')
    statsEl.style.cssText = `
      position: fixed;
      bottom: 20px;
      left: 20px;
      padding: 10px;
      border-radius: 4px;
          border: 2px solid #AAB8C2;
      border-bottom: 5px solid #AAB8C2;
      background-color: #D4E1EA;
    `
    
    return { shieldEl, swordEl, statsEl }
  }

  function _addItemPickupListener(listener) {
    const oldLog = oldConsole.log

    const newLog = (message, ...rest) => {
      oldLog(message, ...rest)

      const object = rest?.[0]
      if (object?.type === 'item') {
        listener(object.item)
      }
    }

    window.console = { ...oldConsole, log: newLog }
  }
})(window.console)

